import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import UserDataService from "../services/UserDataService";
import UserData from "../components/UserData";
import ReactPaginate from "react-paginate";


const UserDataList = () => {

    const navigate = useNavigate();
    const [loading, setLoading ] = useState(true);
    const [usersData, setUsersData ] = useState(null);
    const [currentItems, setCurrentItems] = useState(1);
    const [items, setItems] = useState([]);
    const [pageCount, setPageCount] = useState(1);
    const [itemOffset, setItemOffset] = useState(0);

    const [searchQuery, setSearchQuery] = useState("");
    const [page, setPage] = useState(1);

    const itemsPerPage = 5;
    // const numberOfUsersVistited = page * usersPerPage;

    const onChangeSearchQuery = (e) => {
        const searchQuery = e.target.value;
        setSearchQuery(searchQuery);
      };

    const findByQuery = () => {
    setPage(1);
    //retrieveTutorials();
    };

    useEffect(()=> {
        const fetchData = async() => {
            setLoading(true);
            try {
                const response = await UserDataService.allUserData();
                let data = await response.data;
                let count = data.count;
                let total = data.total;
                console.log('total');
                console.log(total);
                let list = data.list;
                console.log('list');
                console.log(list);
                setUsersData(list);
                let offset = Math.ceil(total / itemsPerPage);
                console.log('offset');
                console.log(offset);
                setPageCount(offset);
                setItems(list);
            } catch (error) {
                console.log(error);
            }
            setLoading(false);
        };
        fetchData();
    }, [itemOffset, itemsPerPage]);

    const handlePageClick = async (data) => {
        console.log(data.selected);
        let currentPage = data.selected + 1;
        const newOffset = (data.selected * itemsPerPage) % items.length; 

        let commentsFormServer = await fetchComments(currentPage);
        console.log(commentsFormServer);
        setItems(commentsFormServer.content);
        setUsersData(commentsFormServer.content);
        setItemOffset(newOffset); 

    };

    const fetchComments = async (currentPage) => {
        const response = await UserDataService.allUserDataFetchPag(currentPage,itemsPerPage);
        const data = await response.data;
        const total = data.count;
        const newOffset = (currentPage * itemsPerPage) % data.length;
        setPageCount(Math.ceil(total / itemsPerPage));
        setItemOffset(newOffset);
        return data.list;
    };

    useEffect(()=> {
        const fetchData = async() => {
            setLoading(true);
            try {
                const response = await UserDataService.allUserData();
                const data = await response.data;
                let count = data.count;
                let total = data.total;
                console.log('total');
                console.log(total);
                let list = data.list;
                console.log('list');
                console.log(list);
                setUsersData(list);
                setItems(list);
                console.log(total / itemsPerPage);
                let offset = Math.ceil(total / itemsPerPage);
                console.log('offset');
                console.log(offset);
                setPageCount(offset);
                setCurrentItems(items.slice(itemOffset, itemsPerPage));
                setPageCount(offset);
            } catch (error) {
                console.log(error);
            }
            setLoading(false);
        };
        fetchData();
    }, []);


    const editUser =  (e, id) => {
        e.preventDefault();
    }

    const deleteUser =  (e, id) => {
        e.preventDefault();
        UserDataService.deleteUserData(id).then((res) => {
           if(usersData){
                setUsersData((prevElement) => {
                    return prevElement.filter((userData) => userData.id !== id);
                });
           }
        });
    };



    return (
    <div className="container mx-auto my-8">
        <div class="heading">Campaign User List</div>  
        <div>
            <button onClick={()=>navigate("/addUserData")} className="h-12 rounded text-white font-semibold bg-slate-600 hover:bg-slate-800 py-2 px-6 font-semibold">
                Add UserData
            </button>
        </div>
        <div className="list row">
            <div className="col-md-8">
                <div className="input-group mb-3">
                <input
                    type="text"
                    className="form-control"
                    placeholder="Search by ...."
                    value={searchQuery}
                    onChange={onChangeSearchQuery}
                />
                <div className="input-group-append">
                    <button className="btn btn-outline-secondary"  type="button" onClick={findByQuery} >
                    Search
                    </button>
                </div>
                </div>
            </div>
        </div>
        <div className="flex shadow border-b">
            {!loading && (
                    <table className="min-w-full table-hover">
                            <thead className="bg-gray-50">
                                <tr>
                                    <th className="text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6">First Name</th>
                                    <th className="text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6">Last Name</th>
                                    <th className="text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6">E-mail id</th>
                                    <th className="text-right font-medium text-gray-500 uppercase tracking-wider py-3 px-6">Actions</th>
                                </tr>
                            </thead>

                                <tbody className="bg-white">
                                {usersData.map((userData) => (
                                        <UserData userData={userData} deleteUser={deleteUser} key={userData.id}></UserData>
                                    ))}
                            </tbody>

                        </table>
            )}

        </div>
        <div>
                {!loading ? (
                <div className="flex shadow border-b">
                    <ReactPaginate
                        nextLabel="next >"
                        breakLabel={'...'}
                        breakClassName={'break-me'}
                        onPageChange={handlePageClick}
                        pageRangeDisplayed={itemsPerPage}
                        displayedPageRange = {itemsPerPage}  
                        pageCount={pageCount}
                        previousLabel="< previous"
                        renderOnZeroPageCount={null}
                        containerClassName={"pagination justify-content-center"}
                        subContainerClassName="pages pagination"
                        pageClassName={"page-item"}
                        pageLinkClassName={"page-link"}
                        previousClassName={"page-item"}
                        previousLinkClassName={"page-link"}
                        nextClassName={"page-item"}
                        nextLinkClassName={"page-link"}
                        breakLinkClassName={"page-link"}
                        activeClassName={"active"}
                        forcePage={currentItems}
                    />
                </div>
             ) : (
				<div>Nothing to display</div>
			)} 
        </div> 
    </div>
    );
} 
export default UserDataList;
