import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import UserDataService from "../services/UserDataService";
import UserData from "../components/UserData";
import ReactPaginate from "react-paginate";


const UserDataList = () => {
    const itemsPerPage = 5;
    const navigate = useNavigate();
    const [loading, setLoading ] = useState(true);
    const [usersData, setUsersData ] = useState(null);
    const [currentItems, setCurrentItems] = useState(1);
    const [items, setItems] = useState([]);

    const [pageCount, setPageCount] = useState([]);
    const [itemOffset, setItemOffset] = useState(0);

    const [searchQuery, setSearchQuery] =  useState("");
    const [page, setPage] = useState(1);
    const [pageSize, setPageSize] = useState(5);
    const [currentPage, setCurrentPage] = useState(0);
    const pageVisited = currentPage * itemsPerPage;


    const onChangeSearchQuery = (e) => {
        const searchQuery = e.target.value;
        setSearchQuery(searchQuery);
      };

    const findByQuery = () => {
        setPage(1);
        retrieveList();
    };



    useEffect(()=> {
        const fetchData = async() => {
            setLoading(true);
            try {
                const response = await UserDataService.allUserData();
                let data = await response.data;
                let count = data.count;
                let total = data.total;
                let list = data.list;
                setUsersData(list);
                let offset = Math.ceil(total / itemsPerPage);
                setPageCount(offset);
                setItems(list);
            } catch (error) {
                console.log(error);
            }
            setLoading(false);
        };
        fetchData();
    }, [itemOffset, itemsPerPage]);


    const changePage = async (data) => {
        console.log(data.selected);
        setCurrentPage(data.selected);
    };

    const getRequestParams = async (searchQuery, page, pageSize) => {
        let params = {};
        if (searchQuery) {
            params["q"] = searchQuery;
        }
        if (page) {
            params["pageNo"] = page - 1;
        }

        if (pageSize) {
            params["pageSize"] = pageSize;
        }

        return params;
    };

    const retrieveList = () => {
        const params = getRequestParams(searchQuery, page, pageSize);

        UserDataService.allUserDataFetchPag(params)
          .then((response) => {
            let data =  response.data;
            let count = data.count;
            let total = data.total;
            let list = data.list;
            let totalPages = Math.ceil(total / itemsPerPage);
            setUsersData(list);
            setPageCount(totalPages);

            console.log(response.data);
          })
          .catch((e) => {
            console.log(e);
          });
      };

      useEffect(retrieveList, [page, pageSize]);
    /**
     * const response = await UserDataService.allUserDataFetchPag(currentPage,itemsPerPage);
        const data = await response.data;
        const total = data.count;
        const newOffset = (currentPage * itemsPerPage) % data.length;
        setItemOffset(newOffset);
        return data.list;
     */


    useEffect(()=> {
        const fetchData = async() => {
            setLoading(true);
            try {
                const response = await UserDataService.allUserData();
                const data = await response.data;
                let count = data.count;
                let total = data.total;
                let list = data.list;
                setUsersData(list);
                setItems(list);
                let offset = Math.ceil(total / itemsPerPage);
                setCurrentItems(items.slice(itemOffset, itemsPerPage));
            } catch (error) {
                console.log(error);
            }
            setLoading(false);
        };
        fetchData();
    }, []);


    const editUser =  (e, id) => {
        e.preventDefault();
    }

    const deleteUser =  (e, id) => {
        e.preventDefault();
        UserDataService.deleteUserData(id).then((res) => {
           if(usersData){
                setUsersData((prevElement) => {
                    return prevElement.filter((userData) => userData.id !== id);
                });
           }
        });
    };



    return (
    <div className="container mx-auto my-8">
        <div className="heading">Company List users</div>  

        <div className="list row">
            <div className="col-md-8">
                <div className="input-group mb-3 search">
                    <div className="input-group-append">
                        <button onClick={()=>navigate("/addUserData")} className="h-10 rounded text-white font-semibold bg-slate-600 hover:bg-slate-800 py-2  px-6 font-semibold">
                        Add UserData
                        </button>
                    </div>
                    <input type="text" className="form-control rounded" placeholder="Search by ...." value={searchQuery} onChange={onChangeSearchQuery} />
                    <div className="input-group-append">
                        <button className="btn btn-outline-secondary"  type="button" onClick={findByQuery} >
                        Search
                        </button>
                    </div>
                </div>
            </div>
        </div>
        <div className="shadow">
            {!loading && (
                    <table className="min-w-full table-hover">
                            <thead className= "thead">
                                <tr>
                                    <th className="text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6">Id</th>
                                    <th className="text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6">First Name</th>
                                    <th className="text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6">Last Name</th>
                                    <th className="text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6">E-mail id</th>
                                    <th className="text-right font-medium text-gray-500 uppercase tracking-wider py-3 px-6">Actions</th>
                                </tr>
                            </thead>

                                <tbody className="bg-white">
                                {usersData.slice(pageVisited, pageVisited + itemsPerPage).map((userData,index) => (
                                        <UserData userData={userData} deleteUser={deleteUser} key={index}></UserData>
                                    ))}
                            </tbody>

                        </table>
            )}

        </div>
        <div>
                {!loading ? (
                <div className="flex shadow border-b">
                    <ReactPaginate
                        previousLabel="< previous"
                        nextLabel="next >"
                        breakLabel={'...'}
                        breakClassName={'break-me'}
                        pageCount={pageCount}
                        onPageChange={changePage}
                        containerClassName={'pagination justify-content-center'}
                        subContainerClassName={'pages pagination'}
                        previousLinkClassName={"page-link"}
                        nextClassName={"page-item"}
                        disabledClassName={"paginationDisable"}
                        activeClassName={"paginationActive"}
                        pageClassName={"page-item"}
                        pageLinkClassName={"page-link"}
                        previousClassName={"page-item"}
                        nextLinkClassName={"page-link"}
                        breakLinkClassName={"page-link"}
                        marginPagesDisplayed={2}
                        pageRangeDisplayed={2}
                        renderOnZeroPageCount={null}
                    />
                </div>
             ) : (
                <div className="px-8 py-8">
                    <div className="font-thin text-2xl tracking-wider">
                        <div>Nothing to display</div>
                    </div>
				</div>
			)} 
        </div> 
    </div>
    );
} 
export default UserDataList;
