import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import UserDataService from "../services/UserDataService";
import UserData from "../components/UserData";

const UserDataList = () => {

    const navigate = useNavigate();
    const [loading, setLoading ] = useState(true);
    const [usersData, setUsersData ] = useState(null);


    useEffect(()=> {
        const fetchData = async() => {
            setLoading(true);
            try {
                const response = await UserDataService.allUserData();
                setUsersData(response.data);
            } catch (error) {
                console.log(error);
            }
            setLoading(false);
        };
        fetchData();
    }, []);


    const editUser =  (e, id) => {
        e.preventDefault();

    }

    const deleteUser =  (e, id) => {
        e.preventDefault();
        UserDataService.deleteUserData(id).then((res) => {
           if(usersData){
                setUsersData((prevElement) => {
                    return prevElement.filter((userData) => userData.id !== id);
                });
           }
        });
    };



    return (
    <div className="container mx-auto my-8">
        <div>
            <button onClick={()=>navigate("/addUserData")} className="h-12 rounded text-white font-semibold bg-slate-600 hover:bg-slate-800 py-2 px-6 font-semibold">
                Add UserData
            </button>
        </div>
        <div className="flex shadow border-b">
            <table className="min-w-full">
                <thead className="bg-gray-50">
                    <tr>
                        <th className="text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6">First Name</th>
                        <th className="text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6">Last Name</th>
                        <th className="text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6">E-mail id</th>
                        <th className="text-right font-medium text-gray-500 uppercase tracking-wider py-3 px-6">Actions</th>
                    </tr>
                </thead>
                {!loading && (
                    <tbody className="bg-white">
                       {usersData.map((userData) => (
                            <UserData userData={userData} deleteUser={deleteUser} key={userData.id}></UserData>
                        ))}
                </tbody>
                )}
            </table>
        </div>
    </div>
    );
} 
export default UserDataList;
